    let i: number = 0;
    while (i < 3) {  // <-- Parser gets confused here
        i = i + 1;
    }
    return 42;
}

// âœ… WORKAROUND
function processArray(arr: Array<number>): number {
    let i: number = 0;
    while (i == 0) {  // Use == instead of <
